	@Override
	public void doorRoads() {
		this.roadHelper(doorCoords);
	}
	
	@Override
	public void spawnHamletDwellers() {
		BlockPos home1 = this.getPosFromArray(villagerCoords1, this.facing);
		if(this.facing == EnumFacing.NORTH) home1 = home1.add(0, 0, 1);
		else if(this.facing == EnumFacing.WEST) home1 = home1.add(1, 0, 0);
		EntityHamletDweller h1 = new EntityHamletDweller(this.world);
		h1.setProfession(EntityHamletDweller.ProfessionsEnum.BARTENDER);
		h1.setHome(home1);
		h1.setVillageCenter(this.villageCenter);
		h1.setPosition(home1.getX(), home1.getY(), home1.getZ());
		this.world.spawnEntity(h1);
		
		BlockPos home2 = this.getPosFromArray(villagerCoords2, this.facing);
		EntityHamletDweller h2 = new EntityHamletDweller(this.world);
		h2.setProfession(EntityHamletDweller.ProfessionsEnum.DRUNK);
		h2.setHome(home2);
		h2.setVillageCenter(this.villageCenter);
		h2.setPosition(home2.getX(), home2.getY(), home2.getZ());
		this.world.spawnEntity(h2);
		
	}
	
	public void setBeds() {
		switch (facing.getHorizontalIndex()) {
		case 0:
			for(int[] n : this.bed) {
				if(n[3]>=7) {
					world.setBlockState(new BlockPos(x-n[0], y+n[1], z-n[2]), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+2)).withProperty(BlockBed.PART, BlockBed.EnumPartType.HEAD));
					world.setBlockState(new BlockPos(x-n[0], y+n[1], z-n[2]).offset(EnumFacing.getHorizontal(n[3]+2).getOpposite()), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+2)));
				}
			}
			break;
		case 1:
			for(int[] n : this.bed) {
				if(n[3]>=7) {
					world.setBlockState(new BlockPos(x+n[2], y+n[1], z-n[0]), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+3)).withProperty(BlockBed.PART, BlockBed.EnumPartType.HEAD));
					world.setBlockState(new BlockPos(x+n[2], y+n[1], z-n[0]).offset(EnumFacing.getHorizontal(n[3]+3).getOpposite()), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+3)));
				}
			}
			break;
		case 2:
			for(int[] n : this.bed) {
				if(n[3]>=7) {
					world.setBlockState(new BlockPos(x+n[0], y+n[1], z+n[2]), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3])).withProperty(BlockBed.PART, BlockBed.EnumPartType.HEAD));
					world.setBlockState(new BlockPos(x+n[0], y+n[1], z+n[2]).offset(EnumFacing.getHorizontal(n[3]).getOpposite()), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3])));
				}
			}
			break;
		case 3:
			for(int[] n : this.bed) {
				if(n[3]>=7) {
					world.setBlockState(new BlockPos(x-n[2], y+n[1], z+n[0]), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+1)).withProperty(BlockBed.PART, BlockBed.EnumPartType.HEAD));
					world.setBlockState(new BlockPos(x-n[2], y+n[1], z+n[0]).offset(EnumFacing.getHorizontal(n[3]+1).getOpposite()), Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal(n[3]+1)));
				}
			}
			break;
		}
		
	}
	
	private IBlockState getBedState(int meta) {
		/*IBlockState state = Blocks.BED.getDefaultState().withProperty(BlockBed.FACING, EnumFacing.getHorizontal((i-((face+1)&3))&3)).withProperty(BlockBed.PART, i>4 ? BlockBed.EnumPartType.HEAD : BlockBed.EnumPartType.FOOT);
		return state;*/
		EnumFacing enumfacing = EnumFacing.getHorizontal(meta);
        return (meta & 8) > 0 ? Blocks.BED.getDefaultState().withProperty(BlockBed.PART, BlockBed.EnumPartType.HEAD).withProperty(BlockBed.FACING, enumfacing).withProperty(BlockBed.OCCUPIED, Boolean.valueOf((meta & 4) > 0)) : Blocks.BED.getDefaultState().withProperty(BlockBed.PART, BlockBed.EnumPartType.FOOT).withProperty(BlockBed.FACING, enumfacing);
    
	}
	
